import React, { useState, useEffect } from 'react';
import { 
  Container, 
  Typography, 
  Paper, 
  Box, 
  Slider, 
  Button,
  Grid,
  Card,
  CardContent 
} from '@mui/material';

const API_URL = 'http://localhost:5000';

function App() {
  const [jointAngles, setJointAngles] = useState([0, 0, 0, 0, 0, 0]);
  const [endPosition, setEndPosition] = useState({ x: 0, y: 0, z: 0 });
  const [serverStatus, setServerStatus] = useState('checking...');

  useEffect(() => {
    // Check if backend is running
    fetch(`${API_URL}/api/health`)
      .then(res => res.json())
      .then(data => setServerStatus('connected'))
      .catch(err => setServerStatus('disconnected'));

    // Load initial configuration
    fetch(`${API_URL}/api/robot/config`)
      .then(res => res.json())
      .then(data => {
        setJointAngles(data.joint_angles);
      })
      .catch(err => console.error('Error loading config:', err));
  }, []);

  const handleJointChange = (index) => (event, value) => {
    const newAngles = [...jointAngles];
    newAngles[index] = value;
    setJointAngles(newAngles);

    // Update backend
    fetch(`${API_URL}/api/update-joints`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ joint_angles: newAngles })
    })
      .then(res => res.json())
      .then(data => {
        setEndPosition({
          x: data.position[0],
          y: data.position[1],
          z: data.position[2]
        });
      })
      .catch(err => console.error('Error updating joints:', err));
  };

  const calculateFK = () => {
    fetch(`${API_URL}/api/forward-kinematics`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ joint_angles: jointAngles })
    })
      .then(res => res.json())
      .then(data => {
        setEndPosition({
          x: data.position[0],
          y: data.position[1],
          z: data.position[2]
        });
      })
      .catch(err => console.error('Error calculating FK:', err));
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h3" align="center" gutterBottom>
        ðŸ¤– Robotic Arm Simulator
      </Typography>
      
      <Box sx={{ mb: 2, textAlign: 'center' }}>
        <Typography variant="body2">
          Backend Status: 
          <span style={{ 
            color: serverStatus === 'connected' ? 'green' : 'red',
            fontWeight: 'bold' 
          }}>
            {' '}{serverStatus}
          </span>
        </Typography>
      </Box>

      <Grid container spacing={3}>
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h5" gutterBottom>
                Joint Controls
              </Typography>
              
              {jointAngles.map((angle, index) => (
                <Box key={index} sx={{ mb: 3 }}>
                  <Typography variant="body2">
                    Joint {index + 1}: {(angle * 180 / Math.PI).toFixed(1)}Â°
                  </Typography>
                  <Slider
                    value={angle}
                    onChange={handleJointChange(index)}
                    min={-Math.PI}
                    max={Math.PI}
                    step={0.01}
                    valueLabelDisplay="auto"
                    valueLabelFormat={(value) => 
                      `${(value * 180 / Math.PI).toFixed(0)}Â°`
                    }
                  />
                </Box>
              ))}
              
              <Button 
                variant="contained" 
                fullWidth 
                onClick={calculateFK}
                sx={{ mt: 2 }}
              >
                Calculate Forward Kinematics
              </Button>
            </CardContent>
          </Card>
        </Grid>

        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h5" gutterBottom>
                End Effector Position
              </Typography>
              
              <Box sx={{ mt: 3 }}>
                <Typography variant="h6">
                  X: {endPosition.x.toFixed(3)} m
                </Typography>
                <Typography variant="h6">
                  Y: {endPosition.y.toFixed(3)} m
                </Typography>
                <Typography variant="h6">
                  Z: {endPosition.z.toFixed(3)} m
                </Typography>
              </Box>
              
              <Box sx={{ mt: 4, p: 2, bgcolor: '#f5f5f5', borderRadius: 2 }}>
                <Typography variant="body2" color="text.secondary">
                  3D Visualization Coming Soon!
                </Typography>
                <Typography variant="caption">
                  Move the joint sliders to see position changes
                </Typography>
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
}

export default App;
